import React, { useEffect, useState } from 'react';

import classes from './BasketItemVisible.module.css';

function BasketItemVisible({ item }) {

    const [inputValue, setInputValue] = useState(1);

    // Для изменения ширины инпута в зависимости от количества разрядов числа
    const changeWidth = (e) => {
        setInputValue(Number(e.target.value))
    }



    // Для просчета цены
    const [price, setPrice] = useState(0);
    const calcPrice = () => {
        let itemPrice = item.item.price.replace(' ', '');
        itemPrice = Number(itemPrice);
        setPrice(itemPrice * inputValue);
    }

    useMemo(() => {
        calcPrice()
        if (inputValue > 99) {
            setInputValue(99)
        }
        if (inputValue < 1) {
            removeItem()
        }
    }, [inputValue])

    return (
        <div className={classes.basketItemVisible}>
            <img src={`/imgs/items/${item.item._id}_img1.jpg`} alt="item_img" className={classes.basketItem__img} />
            <div className={classes.basketItem__infoWrapper}>
                <h3 className={classes.basketItem__name}>{item.item.name}</h3>
                {item.params ? <p className={classes.basketItem__param}>Color: {item.params.color}</p> : false}
                {item.params ? <p className={classes.basketItem__param}>Size: {item.params.size}</p> : false}
                <p className={classes.basketItem__param}>{item.item.code}</p>
            </div>
            <div className={classes.basketItem__countBlock}>
                <img src="/imgs/icons/minus.svg" alt="minus" className={classes.basketItem__btn} onClick={() => setInputValue(inputValue - 1)} />
                <input className={classes.basketItem__input} value={inputValue} style={{ width: `calc(8px * ${inputValue.toString().length} + 16px)` }} onChange={(e) => changeWidth(e)}></input>
                <img src="/imgs/icons/plus.svg" alt="plus" className={classes.basketItem__btn} onClick={() => setInputValue(inputValue + 1)} />
            </div>
            <p className={classes.basketItem__price}>{price.toLocaleString('ru')} р.</p>
            <img src="/imgs/icons/remove.svg" alt="remove" className={classes.basketItem__remove} />
        </div>
    );
}

export default BasketItemVisible;
