import React, { useState, useEffect } from "react";
import { useOutletContext, useLocation, Outlet } from 'react-router-dom';
import { Helmet } from 'react-helmet';

import classes from './Catalog.module.css';

import Item from '../UI/Item/Item.jsx';

const Catalog = ({ navItem }) => {

    const [items, setItems] = useState(null);

    const [limitValue, setLimitValue] = useState(null);
    const [itemsCount, setItemsCount] = useState(null);
    const [allProductsLoaded, setAllProductsLoaded] = useState(false);
    const [skipCount, setSkipCount] = useState(null);

    const pathname = useLocation().pathname
    useEffect(() => {
        if (!pathname.match(/\d/)) { // Проверка есть ли в ссылке цифры (чтобы не перерендерить каталог когда открыт Item)
            setSkipCount(0)
            setLimitValue(3) // Тут устанавливать количество загружаемых товаров за раз
            // setItemsCount(null)
            setAllProductsLoaded(false)
            fetchItems()
        }
    }, [pathname]);

    useEffect(() => {
        console.log(skipCount, limitValue, items)
    }, [skipCount, limitValue, items]);

    async function fetchItems() {
        const res = await fetch(`http://localhost:3001${pathname}?skipCount=${skipCount}&limitValue=${limitValue}`);
        const json = await res.json()
        // if (!json.items[0]) { // Если получаем пустой массив
        //     setAllProductsLoaded(true);
        // }
        setItems(json.items)
        // setSkipCount(limitValue)
        setItemsCount(json.count)
    }

    async function LoadMore() {
        const res = await fetch(`http://localhost:3001${pathname}?skipCount=${skipCount + limitValue}&limitValue=${limitValue}`);
        const json = await res.json()
        // if (!json.items[0]) { // Если получаем пустой массив
        //     setAllProductsLoaded(true);
        // }
        setItems([...items, ...json.items])
        setSkipCount(skipCount + limitValue)
        setItemsCount(json.count)
    }

    const navItems = useOutletContext().navItems;
    const addItemToBasket = useOutletContext().addItemToBasket;

    let title;
    !navItem
        ? title = "La Routine Magazine"
        : navItem == 'magazine'
            ? title = "Magazine / Photo"
            : title = navItem[0].toUpperCase() + navItem.slice(1);

    return (
        <div className={classes.catalog__wrapper}>
            <Helmet>
                <title>{title}</title>
            </Helmet>
            <Outlet context={addItemToBasket} />
            {!items || !items[0] || pathname.match(/\d/)
                ? <h1 className={classes.miss}>Товары отсутвуют.</h1>
                : <div className={classes.catalog}>
                    {items.map((item) => <Item key={item._id} item={item} navItem={navItem} navItems={navItems} pathname={pathname} />)}
                    <button
                        onClick={fetchItems}
                        className={allProductsLoaded ? classes.catalog__btn + " " + classes.disabled : classes.catalog__btn}>
                        Загрузить ещё
                    </button>
                </div>
            }
        </div>
    )
};

export default Catalog;
