import React, { useEffect, useMemo, useState } from 'react';

import classes from './BasketItem.module.css';

import BasketItemRemove from '../BasketItemRemove/BasketItemRemove.jsx';
import BasketItemVisible from '../BasketItemVisible/BasketItemVisible.jsx';

function BasketItem({ item }) {

    const [inputValue, setInputValue] = useState(1);

    // Для изменения ширины инпута в зависимости от количества разрядов числа
    const changeWidth = (e) => {
        setInputValue(Number(e.target.value))
    }

    const [leftTime, setLeftTime] = useState(4);
    // Для удаления товара из корзины
    const [removeItemActive, setRemoveItemActive] = useState(0);
    const removeItem = () => {

        setTimeout(() => {

        }, 4000)
    }

    // Для просчета цены
    const [price, setPrice] = useState(0);
    const calcPrice = () => {
        let itemPrice = item.item.price.replace(' ', '');
        itemPrice = Number(itemPrice);
        setPrice(itemPrice * inputValue);
    }

    // При смене количества товара
    useMemo(() => {
        calcPrice()
        if (inputValue > 99) {
            setInputValue(99)
        }
        if (inputValue < 1) {
            removeItem()
        }
    }, [inputValue])

    return (
        <div className={classes.basketItem}>
            {removeItemActive ? <BasketItemRemove item={item} leftTime={leftTime} /> : 
            <BasketItemVisible />}
        </div>
    );
}

export default BasketItem;
