import React, { useEffect, useState } from "react";

import classes from './Radio.module.css';

const Radio = ({ name, labelText, textarea, register, errors, required }) => {

    return (
        <label className={classes.checkbox__wrapper}>
            <input
                type="radio"
                className={classes.checkbox}
                // className={errors[name] ? inputClasses + " " + classes.inputError : inputClasses}
                name={name}
                {...register(name, { required: required })}
                // aria-invalid={errors[name] ? "true" : "false"}
            />
            {labelText}
            {/* {errors[name] && errors[name].type === "required" && (
                <span className={classes.error}>Обязательное поле</span>
            )} */}
        </label>
    );
};

export default Radio;
