import React, { useState } from "react";
import { useForm } from "react-hook-form";

import classes from './Checkout.module.css';

const Checkout = ({ basket, checkoutIsActive, closeCheckout }) => {

    return (
        <div className={checkoutIsActive ? classes.checkout + " " + classes.active : classes.checkout} onClick={() => closeCheckout()}>
            <div className={classes.checkout__top}>
                <img src="/imgs/icons/checkout__arrow.svg" alt="arrow" className={classes.checkout__arrow} />
                <h3 className={classes.checkout__title}>Ваш заказ</h3>
                <img src="/imgs/icons/cross.svg" alt="cross" />
            </div>
        </div >
    );
};

export default Checkout;
