                {item.params ? <p className={classes.basketItem__param}>Color: {item.params.color}</p> : false}
                {item.params ? <p className={classes.basketItem__param}>Size: {item.params.size}</p> : false}
                <p className={classes.basketItem__param}>{item.item.code}</p>
            </div>
            <div className={classes.basketItem__countBlock}>
                <img src="/imgs/icons/minus.svg" alt="minus" className={classes.basketItem__btn} onClick={() => setInputValue(inputValue - 1)} />
