import React, { useState, useEffect, useMemo } from "react";
import { useOutletContext, useLocation, Outlet } from 'react-router-dom';

import classes from './Catalog.module.css';

import Item from '../UI/Item/Item.jsx';

const Catalog = ({ navItem }) => {

    const [items, setItems] = useState(null);

    const pathname = useLocation().pathname
    useEffect(() => {
        pathname.match(/\d/) // Проверка есть ли в ссылке цифры (чтобы не перерендерить каталог когда открыт Item)
            ? console.log()
            : fetchItems()
    }, [pathname]);

    async function fetchItems() {
        const res = await fetch(`http://localhost:3001${pathname}`);
        const json = await res.json()
        setItems(json)
    }

    const [page, setPage] = useState(3);

    async function loadMore() {
        const res = await fetch(`http://localhost:3001${pathname}?limitCount=${page + 3}`);
        const json = await res.json()
        // setItems(...items, json)
        console.log(json)
        setPage(page + 3)
    }

    const navItems = useOutletContext();

    // Сортировка по новым товарам (хз как работает sort)
    if (items) {
        items.sort((a, b) => {
            return b.new - a.new
        })
    }

    if (!items || !items[0] || pathname.match(/\d/)) { // последнее условие для того чтобы скрывать товары когда открыт один
        return (
            <h1 className={classes.miss}>Товары отсутвуют.</h1>
        )
    } else {
        return (
            <div className={classes.catalog}>
                <Outlet />
                {items.map((item, index) => <Item item={item} navItem={navItem} itemIndex={index} navItems={navItems} />)}
                <button onClick={loadMore} className={classes.catalog__btn}>Загрузить ещё</button>
            </div >
        )
    }

    // return (
    //     <div className={classes.catalog}>
    //         <Outlet />
    //         {/* {!items || !items[0] || pathname.match(/\d/) // последнее условие для того чтобы скрывать товары когда открыт один
    //             ? <h1 className={classes.miss}>Товары отсутвуют.</h1>
    //             : items.map((item, index) => <Item item={item} navItem={navItem} itemIndex={index} navItems={navItems} />) 
    //         } */}
    //     </div >
    // )
};

export default Catalog;
