import React, { useEffect, useMemo, useState } from 'react';

import classes from './BasketItem.module.css';

import BasketItemRemove from '../BasketItemRemove/BasketItemRemove.jsx';
import BasketItemVisible from '../BasketItemVisible/BasketItemVisible.jsx';

function BasketItem({ item }) {

    const [leftTime, setLeftTime] = useState(4);

    // Для удаления товара из корзины
    const [removeItemActive, setRemoveItemActive] = useState(0);
    const removeItem = () => {
        setRemoveItemActive(1);
        const timerId = setTimeout(() => {
            console.log(true)
        }, 4000);
        useMemo(() => {
            if (removeItem == 1) {
                clearTimeout()
            }
        }, [removeItemActive])
    }

    const cancelRemoveItem = () => {
        setRemoveItemActive(1);
    }

    return (
        <div className={classes.basketItem}>
            {removeItemActive
                ? <BasketItemRemove item={item} leftTime={leftTime} cancelRemoveItem={cancelRemoveItem} />
                : <BasketItemVisible item={item} removeItem={removeItem} />
            }
        </div>
    );
}

export default BasketItem;
