import React, { useEffect, useMemo, useState } from 'react';

import classes from './BasketItem.module.css';

function BasketItem({ item }) {

    const [inputValue, setInputValue] = useState(1);

    const changeWidth = (e) => {
        // Добавить проверку на цифру
        setInputValue(e.target.value)
        const width = 12 * e.target.value.length;
        e.target.style.width = width + 'px';
    }

    // useMemo(() => {

    // }, [inputValue])

    return (
        <div className={classes.basketItem}>
            <img src={`/imgs/items/${item.item._id}_img1.jpg`} alt="item_img" className={classes.basketItem__img} />
            <div className={classes.basketItem__infoWrapper}>
                <h3 className={classes.basketItem__name}>{item.item.name}</h3>
                {item.params ? <p className={classes.basketItem__param}>Color: {item.params.color}</p> : false}
                {item.params ? <p className={classes.basketItem__param}>Size: {item.params.size}</p> : false}
                <p className={classes.basketItem__param}>{item.item.code}</p>
            </div>
            <div className={classes.basketItem__countBlock}>
                <img src="/imgs/icons/minus.svg" alt="minus" className={classes.basketItem__btn} onClick={() => setInputValue(inputValue - 1)} />
                <input className={classes.basketItem__input} value={inputValue} onChange={(e) => changeWidth(e)}></input>
                <img src="/imgs/icons/plus.svg" alt="plus" className={classes.basketItem__btn} onClick={() => setInputValue(inputValue + 1)} />
            </div>
        </div>
    );
}

export default BasketItem;
