import React, { useEffect } from 'react';

import classes from './BasketItem.module.css';

function BasketItem({ item }) {
if (item.params.color) {
    console.log(item.params.color)
}


    return (
        <div className={classes.basketItem}>
            <img src={`/imgs/items/${item.item._id}_img1.jpg`} alt="item_img" className={classes.basketItem__img} />
            <div className={classes.basketItem__itemInfoWrapper}>
                <h3 className={classes.basketItem__itemName}>{item.item.name}</h3>
                {/* {item.params.color ? <p className={classes.basketItem__itemParam}>Color: {item.params.color}</p> : false}
                {item.params.size ? <p className={classes.basketItem__itemParam}>Size: {item.params.size}</p> : false} */}
                <p className={classes.basketItem__itemParam}>{item.item.code}</p>
            </div>
        </div>
    );
}

export default BasketItem;
