        setInputValue(Number(e.target.value))
    }

    const [price, setPrice] = useState(0);
    const calcPrice = () => {
        let itemPrice = item.item.price.replace(' ', '');
        itemPrice = Number(itemPrice);
        setPrice(itemPrice * inputValue);
    }

