import React, { useEffect, useState } from 'react';

import classes from './BasketItemRemove.module.css';

function BasketItemRemove({ item, leftTime, cancelRemoveItem }) {

    // onClick={() => removeItem()}

    const dashArray = 2 * Math.PI * 11;

    const [dashOffset, setDashOffset] = useState(-69);
    const timer = () => {
        setInterval(() => {
            setDashOffset(dashOffset => dashOffset + 1);
        }, 58)
    }

    useEffect(() => {
        timer()
    }, [])

    return (
        <div className={classes.basketItemRemove}>
            <div className={classes.basketItemRemove__left}>
                <div className={classes.basketItemRemove__timer}>
                    <svg height="25" width="25">
                        <circle cx="13" cy="12" r="11" stroke="red" strokeWidth="2" fill="none" strokeDasharray={dashArray} strokeDashoffset={dashOffset} />
                    </svg>
                    <p>{leftTime}</p>
                </div>
                <p className={classes.basketItemRemove__deleteMessage}>Вы удалили "{item.item.name}"</p>
            </div>
            <p className={classes.basketItemRemove__returnText} onClick={() => cancelRemoveItem()}>Вернуть</p>
        </div>
    );
}

export default BasketItemRemove;
