import React from "react";
import {useForm} from 'react-hook-form';

import classes from './Input.module.css';

const Input = ({ name, labelText, placeholder, required, textarea }) => {

    const { register, formState: { errors } } = useForm();

    return (
        <div className={classes.input__wrapper}>
            <label htmlFor={name} className={classes.input__label}>{labelText}</label>
            {textarea
                ? <textarea
                    placeholder={placeholder}
               
                    name={name}
                    {...register({ name }, { required: required })}
                />
                : <input
                    placeholder={placeholder}
                    className={errors.name ? classes.input + " " + classes.textarea + " " + classes.inputError : classes.input + " " + classes.textarea}
                    name={name}
                    {...register({ name }, { required: required })}
                />
            }

            {errors.name && errors.name.type === "required" && (
                <span className={classes.error}>Обязательное поле</span>
            )}
        </div>
    );
};

export default Input;
